// =============================================================================
// ğŸµ Music Platform Component - Standalone Version
// =============================================================================
// This is a standalone music platform component that doesn't require QElement.
// It showcases a music streaming interface with artists, playlists, and ratings.
// =============================================================================

import React, { useState } from 'react';

// Music Platform Components
const MusicPlatform = () => {
  const [activeTab, setActiveTab] = useState('discover');
  const [selectedArtist, setSelectedArtist] = useState<any>(null);
  const [userRating, setUserRating] = useState(0);

  const artists = [
    { id: 1, name: 'Luna Eclipse', genre: 'Electronic', followers: '2.3M', rating: 4.8, image: 'ğŸµ' },
    { id: 2, name: 'Neon Dreams', genre: 'Synthwave', followers: '1.8M', rating: 4.9, image: 'ğŸ¶' },
    { id: 3, name: 'Crimson Waves', genre: 'Rock', followers: '3.1M', rating: 4.7, image: 'ğŸ¸' },
    { id: 4, name: 'Digital Pulse', genre: 'EDM', followers: '4.2M', rating: 4.9, image: 'ğŸ§' },
    { id: 5, name: 'Midnight Sonata', genre: 'Classical', followers: '1.5M', rating: 4.6, image: 'ğŸ¼' },
    { id: 6, name: 'Electric Storm', genre: 'Metal', followers: '2.7M', rating: 4.8, image: 'âš¡' }
  ];

  const trendingSongs = [
    { title: 'Digital Sunset', artist: 'Luna Eclipse', plays: '12.4M', duration: '3:42' },
    { title: 'Neon Nights', artist: 'Neon Dreams', plays: '8.9M', duration: '4:15' },
    { title: 'Crimson Tide', artist: 'Crimson Waves', plays: '15.2M', duration: '3:28' },
    { title: 'Electric Dreams', artist: 'Digital Pulse', plays: '22.1M', duration: '3:55' }
  ];

  return (
    <QElementTooltip
      elementId="qelement-provider"
      title="QElementProvider - Root Theme Container"
      description="The main provider that establishes the parent-child styling system and theme management"
      codeExample={`import { QElementProvider, QElementComponent } from 'qelements';

function App() {
  return (
    <QElementProvider>
      {/* Global theme context for all children */}
      <QElementComponent elementId="app-container">
        <div style={{ 
          background: 'linear-gradient(135deg, #1a1a1a, #2d1b1b)',
          minHeight: '100vh'
        }}>
          {/* All children inherit the theme */}
        </div>
      </QElementComponent>
    </QElementProvider>
  );
}`}
      beginnerExplanation="QElementProvider is like the 'master control panel' for your app's styling. It's like having a central theme manager that all your components can access. Think of it as the 'styling foundation' - everything else gets its base styling from here, but can still customize as needed."
      advancedExplanation="QElementProvider creates a React Context that manages the entire styling hierarchy. It establishes the root theme, handles style inheritance through the component tree, and provides styling context to all QElementComponent instances. This enables cascading styles with React component composition, similar to CSS inheritance but with more control and flexibility."
      features={[
        "ğŸ¨ Establishes root theme and global styles",
        "ğŸ‘¨â€ğŸ‘©â€ğŸ‘§â€ğŸ‘¦ Enables parent-child style inheritance", 
        "ğŸ”— Provides styling context to all children",
        "âš¡ Manages theme switching and dynamic updates",
        "ğŸ¯ Centralized style management"
      ]}
    >
      <QElementProvider>
        <div style={{ minHeight: '100vh', background: 'linear-gradient(135deg, #1a1a1a 0%, #2d1b1b 50%, #1a1a1a 100%)' }}>
        {/* Header */}
        <QElementTooltip
          elementId="header-tooltip"
          title="QElementComponent - Header Container"
          description="A styled container that inherits from the parent QElementProvider and can override styles"
          codeExample={`<QElementComponent elementId="header">
  <header style={{
    padding: '1rem 2rem',
    background: 'linear-gradient(90deg, #000000 0%, #2d1b1b 50%, #000000 100%)',
    borderBottom: '2px solid #dc2626',
    boxShadow: '0 4px 20px rgba(220, 38, 38, 0.3)'
  }}>
    <h1>ğŸµ SoundWave</h1>
    {/* Header content inherits theme but can override */}
  </header>
</QElementComponent>`}
          beginnerExplanation="QElementComponent is like a 'smart container' that knows how to style itself. It automatically gets the base styling from its parent (like colors and fonts), but you can also give it its own special styling. It's like having a template that follows the rules but can still be customized."
          advancedExplanation="QElementComponent creates a styled wrapper that participates in the QElements styling hierarchy. It receives inherited styles from parent components and can apply its own local styles through the style prop. The elementId enables style targeting, inheritance management, and dynamic style updates within the component tree."
          features={[
            "ğŸ¨ Inherits styles from parent QElementProvider",
            "ğŸ¯ Applies local styles via style prop",
            "ğŸ‘¨â€ğŸ‘©â€ğŸ‘§â€ğŸ‘¦ Participates in parent-child styling hierarchy",
            "ğŸ” Enables style targeting via elementId",
            "âš¡ Supports dynamic style updates"
          ]}
        >
          <QElementComponent elementId="header">
            <header style={{ 
              padding: '1rem 2rem',
              background: 'linear-gradient(90deg, #000000 0%, #2d1b1b 50%, #000000 100%)',
              borderBottom: '2px solid #dc2626',
              boxShadow: '0 4px 20px rgba(220, 38, 38, 0.3)'
            }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', maxWidth: '1200px', margin: '0 auto' }}>
              <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
                <h1 style={{ color: '#ffffff', margin: 0, fontSize: '2rem', fontWeight: 'bold' }}>
                  ğŸµ SoundWave
                </h1>
                <span style={{ color: '#dc2626', fontSize: '0.9rem', fontWeight: '500' }}>
                  Music Sharing Platform
                </span>
              </div>
              <nav style={{ display: 'flex', gap: '2rem' }}>
                {['discover', 'artists', 'trending', 'profile'].map(tab => (
                  <button
                    key={tab}
                    onClick={() => setActiveTab(tab)}
                    style={{
                      color: activeTab === tab ? '#dc2626' : '#ffffff',
                      background: 'none',
                      border: 'none',
                      fontSize: '1rem',
                      fontWeight: activeTab === tab ? '600' : '400',
                      cursor: 'pointer',
                      textTransform: 'capitalize',
                      borderBottom: activeTab === tab ? '2px solid #dc2626' : '2px solid transparent',
                      padding: '0.5rem 0',
                      transition: 'all 0.3s ease'
                    }}
                  >
                    {tab}
                  </button>
                ))}
              </nav>
            </div>
          </header>
        </QElementComponent>
        </QElementTooltip>

        {/* Main Content */}
        <QElementComponent elementId="main-content">
          <main style={{ maxWidth: '1200px', margin: '0 auto', padding: '2rem' }}>
            {activeTab === 'discover' && (
              <div>
                <h2 style={{ color: '#ffffff', marginBottom: '2rem', fontSize: '2rem' }}>
                  ğŸ” Discover New Music
                </h2>
                <div style={{ 
                  display: 'grid', 
                  gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', 
                  gap: '1.5rem' 
                }}>
                  {artists.map(artist => (
                    <QElementTooltip
                      key={artist.id}
                      elementId={`artist-card-${artist.id}`}
                      title="QElementComponent - Artist Card"
                      description="Individual styled component with hover effects, animations, and interactive behaviors"
                      codeExample={`<QElementComponent elementId="artist-card">
  <div style={{
    background: 'linear-gradient(145deg, #2a2a2a 0%, #1a1a1a 100%)',
    borderRadius: '12px',
    padding: '1.5rem',
    border: '1px solid #dc2626',
    transition: 'all 0.3s ease',
    cursor: 'pointer'
  }}
  onMouseEnter={(e) => {
    e.currentTarget.style.transform = 'translateY(-5px)';
    e.currentTarget.style.boxShadow = '0 8px 25px rgba(220, 38, 38, 0.4)';
  }}>
    <img src={artist.image} alt={artist.name} />
    <h3>{artist.name}</h3>
    <p>{artist.genre}</p>
  </div>
</QElementComponent>`}
                      beginnerExplanation="This is like a 'smart card' that knows how to style itself and respond to user interactions. It gets the basic layout from its parent (like being in a grid), but has its own special styling and can do cool things like hover animations. It's like having a template that you can use over and over again, each with its own personality."
                      advancedExplanation="This QElementComponent demonstrates advanced styling patterns including local style overrides, interactive behaviors, and dynamic style updates. It inherits the grid layout from its parent but applies its own background, border, and transition styles. The hover effects showcase QElements' flexibility with dynamic styling and event handling."
                      features={[
                        "ğŸ“± Inherits grid layout from parent",
                        "ğŸ¨ Applies local card styling",
                        "âœ¨ Handles hover animations and transitions",
                        "ğŸ¯ Demonstrates style composition",
                        "âš¡ Supports interactive behaviors"
                      ]}
                    >
                      <QElementComponent key={artist.id} elementId="artist-card">
                        <div style={{
                        background: 'linear-gradient(145deg, #2a2a2a 0%, #1a1a1a 100%)',
                        borderRadius: '12px',
                        padding: '1.5rem',
                        border: '1px solid #dc2626',
                        cursor: 'pointer',
                        transition: 'all 0.3s ease',
                        boxShadow: '0 4px 15px rgba(0,0,0,0.3)'
                      }}
                      onMouseEnter={(e) => {
                        e.currentTarget.style.transform = 'translateY(-5px)';
                        e.currentTarget.style.boxShadow = '0 8px 25px rgba(220, 38, 38, 0.4)';
                      }}
                      onMouseLeave={(e) => {
                        e.currentTarget.style.transform = 'translateY(0)';
                        e.currentTarget.style.boxShadow = '0 4px 15px rgba(0,0,0,0.3)';
                      }}
                      onClick={() => setSelectedArtist(artist)}
                      >
                        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', marginBottom: '1rem' }}>
                          <div style={{ fontSize: '3rem' }}>{artist.image}</div>
                          <div>
                            <h3 style={{ color: '#ffffff', margin: '0 0 0.5rem 0', fontSize: '1.2rem' }}>
                              {artist.name}
                            </h3>
                            <p style={{ color: '#dc2626', margin: '0', fontSize: '0.9rem' }}>
                              {artist.genre}
                            </p>
                          </div>
                        </div>
                        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                          <span style={{ color: '#ffffff', fontSize: '0.9rem' }}>
                            {artist.followers} followers
                          </span>
                          <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                            <span style={{ color: '#ffd700', fontSize: '1.1rem' }}>â­</span>
                            <span style={{ color: '#ffffff', fontSize: '0.9rem' }}>
                              {artist.rating}
                            </span>
                          </div>
                        </div>
                      </div>
                    </QElementComponent>
                    </QElementTooltip>
                  ))}
                </div>
              </div>
            )}

            {activeTab === 'artists' && selectedArtist && (
              <div>
                <div style={{ 
                  background: 'linear-gradient(145deg, #2a2a2a 0%, #1a1a1a 100%)',
                  borderRadius: '12px',
                  padding: '2rem',
                  border: '2px solid #dc2626',
                  marginBottom: '2rem'
                }}>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '2rem', marginBottom: '2rem' }}>
                    <div style={{ fontSize: '5rem' }}>{selectedArtist.image}</div>
                    <div>
                      <h2 style={{ color: '#ffffff', margin: '0 0 0.5rem 0', fontSize: '2.5rem' }}>
                        {selectedArtist.name}
                      </h2>
                      <p style={{ color: '#dc2626', margin: '0 0 1rem 0', fontSize: '1.2rem' }}>
                        {selectedArtist.genre} â€¢ {selectedArtist.followers} followers
                      </p>
                      <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
                        <span style={{ color: '#ffd700', fontSize: '1.5rem' }}>â­</span>
                        <span style={{ color: '#ffffff', fontSize: '1.2rem' }}>
                          {selectedArtist.rating}/5.0
                        </span>
                      </div>
                    </div>
                  </div>

                  <div style={{ marginBottom: '2rem' }}>
                    <h3 style={{ color: '#ffffff', marginBottom: '1rem' }}>Rate This Artist</h3>
                    <div style={{ display: 'flex', gap: '0.5rem' }}>
                      {[1, 2, 3, 4, 5].map(star => (
                        <button
                          key={star}
                          onClick={() => setUserRating(star)}
                          style={{
                            background: 'none',
                            border: 'none',
                            fontSize: '2rem',
                            cursor: 'pointer',
                            color: star <= userRating ? '#ffd700' : '#666666',
                            transition: 'color 0.2s ease'
                          }}
                        >
                          â­
                        </button>
                      ))}
                    </div>
                    {userRating > 0 && (
                      <p style={{ color: '#dc2626', marginTop: '0.5rem' }}>
                        Thanks for rating! You gave {userRating} stars.
                      </p>
                    )}
                  </div>
                </div>
              </div>
            )}

            {activeTab === 'trending' && (
              <div>
                <h2 style={{ color: '#ffffff', marginBottom: '2rem', fontSize: '2rem' }}>
                  ğŸ”¥ Trending Now
                </h2>
                <div style={{ 
                  background: 'linear-gradient(145deg, #2a2a2a 0%, #1a1a1a 100%)',
                  borderRadius: '12px',
                  padding: '1.5rem',
                  border: '1px solid #dc2626'
                }}>
                  {trendingSongs.map((song, index) => (
                    <QElementComponent key={index} elementId="trending-song">
                      <div style={{
                        display: 'flex',
                        justifyContent: 'space-between',
                        alignItems: 'center',
                        padding: '1rem',
                        borderBottom: index < trendingSongs.length - 1 ? '1px solid #333333' : 'none',
                        cursor: 'pointer',
                        transition: 'background-color 0.2s ease'
                      }}
                      onMouseEnter={(e) => {
                        e.currentTarget.style.backgroundColor = '#333333';
                      }}
                      onMouseLeave={(e) => {
                        e.currentTarget.style.backgroundColor = 'transparent';
                      }}
                      >
                        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
                          <span style={{ color: '#dc2626', fontSize: '1.2rem', fontWeight: 'bold' }}>
                            #{index + 1}
