import React, { useState } from 'react';
import { QElementProvider, QElementComponent } from './qelements';

const CodeShowcase = () => {
  const codeSections = {
    overview: {
      title: 'QElements Overview',
      description: 'How QElements powers the music platform with parent-child styling',
      code: `// Main App Structure with QElementProvider
import { QElementProvider, QElementComponent } from './qelements';

const MusicPlatform = () => {
  return (
    <QElementProvider>
      <QElementComponent elementId="header">
        {/* Header inherits global theme */}
      </QElementComponent>

      <QElementComponent elementId="main-content">
        {/* Content area with responsive design */}
      </QElementComponent>
    </QElementProvider>
  );
};`
    },
    theming: {
      title: 'Theme System',
      description: 'Black and red gradient theme applied through QElements',
      code: `// Theme Configuration
const theme = {
  primary: '#dc2626',      // Red accent
  background: 'linear-gradient(135deg, #1a1a1a 0%, #2d1b1b 50%, #1a1a1a 100%)',
  text: '#ffffff',
  card: 'linear-gradient(145deg, #2a2a2a 0%, #1a1a1a 100%)',
  border: '#dc2626'
};

// Applied through QElementComponent
<QElementComponent elementId="artist-card">
  <div style={{
    background: theme.card,
    border: \`1px solid \${theme.border}\`,
    color: theme.text
  }}>
    {/* Card content */}
  </div>
</QElementComponent>`
    },
    responsive: {
      title: 'Responsive Design',
      description: 'QElements responsive system for mobile and desktop',
      code: `// Responsive Grid Layout
<QElementComponent elementId="artist-grid">
  <div style={{ 
    display: 'grid', 
    gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', 
    gap: '1.5rem' 
  }}>
    {artists.map(artist => (
      <QElementComponent key={artist.id} elementId="artist-card">
        {/* Artist card with responsive sizing */}
      </QElementComponent>
    ))}
  </div>
</QElementComponent>

// Mobile-first approach with QElements
const responsiveConfig = {
  mobile: { padding: '1rem', fontSize: '0.9rem' },
  tablet: { padding: '1.5rem', fontSize: '1rem' },
  desktop: { padding: '2rem', fontSize: '1.1rem' }
};`
    },
    interactions: {
      title: 'Interactive Elements',
      description: 'Hover effects and state management with QElements',
      code: `// Interactive Artist Cards
<QElementComponent elementId="artist-card">
  <div 
    style={{
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      // Base styles from parent theme
    }}
    onMouseEnter={(e) => {
      // QElements handles style inheritance
      e.currentTarget.style.transform = 'translateY(-5px)';
      e.currentTarget.style.boxShadow = '0 8px 25px rgba(220, 38, 38, 0.4)';
    }}
    onMouseLeave={(e) => {
      e.currentTarget.style.transform = 'translateY(0)';
      e.currentTarget.style.boxShadow = '0 4px 15px rgba(0,0,0,0.3)';
    }}
  >
    {/* Card content */}
  </div>
</QElementComponent>`
    },
    animations: {
      title: 'Animations & Transitions',
      description: 'Smooth animations powered by QElements animation system',
      code: `// QElements Animation Integration
import { useQElementAnimation } from './qelements';

const AnimatedCard = () => {
  const { addAnimation, removeAnimation } = useQElementAnimation('card-animation');

  const handleHover = () => {
    addAnimation({
      name: 'fadeIn',
      duration: 300,
      timingFunction: 'ease-in-out'
    });
  };

  return (
    <QElementComponent elementId="animated-card">
      {/* Animated content */}
    </QElementComponent>
  );
};`
    }
  };

  const [activeSection, setActiveSection] = useState<keyof typeof codeSections>('overview');

  return (
    <QElementProvider>
      <QElementComponent elementId="code-showcase">
        <div style={{
          minHeight: '100vh',
          background: 'linear-gradient(135deg, #0f0f0f 0%, #1a0a0a 50%, #0f0f0f 100%)',
          color: '#ffffff',
          fontFamily: 'Monaco, Consolas, "Courier New", monospace'
        }}>
          {/* Header */}
          <QElementComponent elementId="code-header">
            <header style={{
              background: 'linear-gradient(90deg, #000000 0%, #1a0a0a 50%, #000000 100%)',
              borderBottom: '2px solid #dc2626',
              padding: '1rem 2rem'
            }}>
              <div style={{ maxWidth: '1200px', margin: '0 auto' }}>
                <h1 style={{ color: '#dc2626', margin: '0', fontSize: '1.8rem' }}>
                  💻 QElements Code Showcase
                </h1>
                <p style={{ color: '#ffffff', margin: '0.5rem 0 0 0', fontSize: '1rem' }}>
                  How QElements powers the SoundWave music platform
                </p>
              </div>
            </header>
          </QElementComponent>

          <QElementComponent elementId="code-content">
            <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '2rem' }}>
              {/* Navigation */}
              <QElementComponent elementId="code-navigation">
                <nav style={{ marginBottom: '2rem' }}>
                  <div style={{ display: 'flex', gap: '1rem', flexWrap: 'wrap' }}>
                    {Object.entries(codeSections).map(([key, section]) => (
                      <button
                        key={key}
                        onClick={() => setActiveSection(key as keyof typeof codeSections)}
                        style={{
                          background: activeSection === key 
                            ? 'linear-gradient(45deg, #dc2626, #ff4444)' 
                            : 'transparent',
                          color: '#ffffff',
                          border: '1px solid #dc2626',
                          padding: '0.75rem 1.5rem',
                          borderRadius: '6px',
                          cursor: 'pointer',
                          fontSize: '0.9rem',
                          fontWeight: '500',
                          transition: 'all 0.3s ease'
                        }}
                      >
                        {section.title}
                      </button>
                    ))}
                  </div>
                </nav>
              </QElementComponent>

              {/* Content */}
              <QElementComponent elementId="code-section">
                <div style={{
                  background: 'linear-gradient(145deg, #1a1a1a 0%, #0f0f0f 100%)',
                  borderRadius: '12px',
                  padding: '2rem',
                  border: '1px solid #dc2626',
                  boxShadow: '0 4px 20px rgba(0,0,0,0.5)'
                }}>
                  <h2 style={{ color: '#dc2626', marginBottom: '1rem', fontSize: '1.5rem' }}>
                    {codeSections[activeSection].title}
                  </h2>
                  <p style={{ color: '#ffffff', marginBottom: '2rem', fontSize: '1rem', lineHeight: '1.6' }}>
                    {codeSections[activeSection].description}
                  </p>

                  <div style={{
                    background: '#000000',
                    borderRadius: '8px',
                    padding: '1.5rem',
                    border: '1px solid #333333',
                    overflow: 'auto'
                  }}>
                    <pre style={{
                      color: '#00ff00',
                      fontSize: '0.9rem',
                      lineHeight: '1.5',
                      margin: '0',
                      whiteSpace: 'pre-wrap'
                    }}>
                      <code>{codeSections[activeSection].code}</code>
                    </pre>
                  </div>
                </div>
              </QElementComponent>

              {/* Features List */}
              <QElementComponent elementId="features-list">
                <div style={{ marginTop: '2rem' }}>
                  <h3 style={{ color: '#dc2626', marginBottom: '1rem' }}>🎯 QElements Features Demonstrated</h3>
                  <div style={{ 
                    display: 'grid', 
                    gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', 
                    gap: '1rem' 
                  }}>
                    {[
                      { icon: '👨‍👩‍👧‍👦', title: 'Parent-Child Styling', desc: 'Header, main, footer inherit theme' },
                      { icon: '🎨', title: 'Theme System', desc: 'Black/red gradient applied globally' },
                      { icon: '📱', title: 'Responsive Design', desc: 'Grid layouts adapt to screen size' },
                      { icon: '✨', title: 'Animations', desc: 'Smooth hover effects and transitions' },
                      { icon: '🔧', title: 'State Management', desc: 'Interactive elements with QElements' },
                      { icon: '⚡', title: 'Performance', desc: 'Optimized rendering and updates' }
                    ].map((feature, index) => (
                      <div key={index} style={{
                        background: 'linear-gradient(145deg, #2a2a2a 0%, #1a1a1a 100%)',
                        borderRadius: '8px',
                        padding: '1rem',
                        border: '1px solid #dc2626',
                        textAlign: 'center'
                      }}>
                        <div style={{ fontSize: '2rem', marginBottom: '0.5rem' }}>{feature.icon}</div>
                        <h4 style={{ color: '#ffffff', margin: '0 0 0.5rem 0', fontSize: '1rem' }}>
                          {feature.title}
                        </h4>
                        <p style={{ color: '#dc2626', margin: '0', fontSize: '0.8rem' }}>
                          {feature.desc}
                        </p>
                      </div>
                    ))}
                  </div>
                </div>
              </QElementComponent>
            </div>
          </QElementComponent>
        </div>
      </QElementComponent>
    </QElementProvider>
  );
};

export default CodeShowcase;
