import React, { useState } from 'react';
import { 
  QElementProvider, 
  QElementComponent, 
  useQElementStyle
} from './qelements';

// Import template components
import BasicTemplate from './templates/BasicTemplate';
import ThemeTemplate from './templates/ThemeTemplate';
import ResponsiveTemplate from './templates/ResponsiveTemplate';
import AnimationTemplate from './templates/AnimationTemplate';

// Basic Parent-Child Demo using the actual QElements API
const BasicParentChildDemo = () => {
  const { updateParent } = useQElementStyle('basic_demo');
  const [parentStyle, setParentStyle] = useState({
    padding: 20,
    margin: 10,
    backgroundColor: '#f0f0f0',
    borderRadius: 8,
    border: '2px solid #ddd'
  });

  const handleParentUpdate = (newStyle: any) => {
    const updatedStyle = { ...parentStyle, ...newStyle };
    setParentStyle(updatedStyle);
    updateParent(updatedStyle);
  };

  return (
    <div style={{ marginBottom: '2rem' }}>
      <h3>ðŸŽ¯ Basic Parent-Child Demo</h3>
      <p>This parent controls the base styles for all children with the same elementId.</p>

      <div style={{ marginBottom: '1rem', display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
        <button onClick={() => handleParentUpdate({ backgroundColor: '#e3f2fd' })}>
          Blue Theme
        </button>
        <button onClick={() => handleParentUpdate({ backgroundColor: '#f3e5f5' })}>
          Purple Theme
        </button>
        <button onClick={() => handleParentUpdate({ backgroundColor: '#e8f5e8' })}>
          Green Theme
        </button>
        <button onClick={() => handleParentUpdate({ padding: 30, borderRadius: 15 })}>
          More Padding
        </button>
        <button onClick={() => handleParentUpdate({ border: '3px solid #ff6b6b' })}>
          Red Border
        </button>
      </div>

      <QElementComponent elementId="basic_demo">
        <h4>Parent Container</h4>
        <p>I'm the parent! My styles affect all my children.</p>

        <BasicChild />
        <BasicChild />
        <BasicChild />
      </QElementComponent>
    </div>
  );
};

const BasicChild = () => {
  const { override, reset, getComputed } = useQElementStyle('basic_demo');
  const [overrides, setOverrides] = useState<any>({});

  const handleOverride = (newOverrides: any) => {
    const updatedOverrides = { ...overrides, ...newOverrides };
    setOverrides(updatedOverrides);
    override(updatedOverrides);
  };

  const handleReset = () => {
    setOverrides({});
    reset();
  };

  const computedStyles = getComputed();

  return (
    <div style={{ 
      margin: '10px 0', 
      padding: '10px', 
      border: '1px dashed #999',
      backgroundColor: 'rgba(255,255,255,0.5)'
    }}>
      <h5>Child Component</h5>
      <p>I inherit from my parent but can override specific properties!</p>

      <div style={{ marginBottom: '10px', display: 'flex', gap: '5px', flexWrap: 'wrap' }}>
        <button onClick={() => handleOverride({ backgroundColor: '#ffeb3b' })}>
          Yellow BG
        </button>
        <button onClick={() => handleOverride({ backgroundColor: '#4caf50', color: 'white' })}>
          Green BG
        </button>
        <button onClick={() => handleOverride({ padding: 15, borderRadius: 20 })}>
          Custom Padding
        </button>
        <button onClick={() => handleOverride({ border: '2px solid #2196f3' })}>
          Blue Border
        </button>
        <button onClick={handleReset}>
          Reset to Parent
        </button>
      </div>

      <details style={{ fontSize: '12px', marginTop: '10px' }}>
        <summary>Current Computed Styles</summary>
        <pre style={{ fontSize: '10px', background: '#f5f5f5', padding: '5px' }}>
          {JSON.stringify(computedStyles, null, 2)}
        </pre>
      </details>
    </div>
  );
};

