// =============================================================================
// üë∂ CHILD FILE - Parent-Child Demo Component
// =============================================================================
// This is a CHILD FILE that inherits styles from parent files.
// It only specifies child-specific overrides and inherits all base styles.
// Parent styles are defined in: ../parent/ParentStyles.ts
// =============================================================================

import React, { useState } from 'react';
import { QElementComponent } from 'qelements';
import { useParentStyles } from '../parent/ParentStylesProvider';

const ParentChildDemo = () => {
  const [activeTheme, setActiveTheme] = useState('default');
  const { updateRootTheme } = useParentStyles();

  const handleThemeChange = (theme: string) => {
    setActiveTheme(theme);

    // Update the root theme to affect all children
    if (theme === 'light') {
      updateRootTheme({
        background: 'linear-gradient(135deg, #f8f9fa 0%, #e9ecef 50%, #dee2e6 100%)',
        color: '#212529',
      });
    } else if (theme === 'blue') {
      updateRootTheme({
        background: 'linear-gradient(135deg, #0a0a2e 0%, #16213e 25%, #0f3460 50%, #16213e 75%, #0a0a2e 100%)',
        color: '#e3f2fd',
      });
    } else {
      updateRootTheme({
        background: 'linear-gradient(135deg, #0a0a0a 0%, #1a0a0a 25%, #2d1b1b 50%, #1a0a0a 75%, #0a0a0a 100%)',
        color: '#ffffff',
      });
    }
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h2 style={{ color: 'inherit', marginBottom: '20px' }}>
        üéØ QElements Parent-Child System Demo
      </h2>

      {/* Theme Controls */}
      <QElementComponent elementId="card">
        <div>
          <h3 style={{ color: 'inherit', marginBottom: '15px' }}>
            üé® Theme Controls - Change Parent Styles
          </h3>
          <p style={{ color: 'inherit', marginBottom: '15px' }}>
            Click these buttons to change the parent theme. All children will automatically inherit the new styles!
          </p>

          <div style={{ display: 'flex', gap: '10px', marginBottom: '20px' }}>
            <QElementComponent elementId="button">
              <button
                onClick={() => handleThemeChange('default')}
                style={{
                  background: activeTheme === 'default' 
                    ? 'linear-gradient(135deg, #dc2626 0%, #ff6b6b 50%, #dc2626 100%)' 
                    : undefined,
                }}
              >
                üåô Default Theme
              </button>
            </QElementComponent>

            <QElementComponent elementId="button">
              <button
                onClick={() => handleThemeChange('light')}
                style={{
                  background: activeTheme === 'light' 
                    ? 'linear-gradient(135deg, #dc2626 0%, #ff6b6b 50%, #dc2626 100%)' 
                    : undefined,
                }}
              >
                ‚òÄÔ∏è Light Theme
              </button>
            </QElementComponent>

            <QElementComponent elementId="button">
              <button
                onClick={() => handleThemeChange('blue')}
                style={{
                  background: activeTheme === 'blue' 
                    ? 'linear-gradient(135deg, #dc2626 0%, #ff6b6b 50%, #dc2626 100%)' 
                    : undefined,
                }}
              >
                üåä Blue Theme
              </button>
            </QElementComponent>
          </div>
        </div>
      </QElementComponent>

      {/* Parent-Child Examples */}
      <QElementComponent elementId="card">
        <div>
          <h3 style={{ color: 'inherit', marginBottom: '15px' }}>
            üë®‚Äçüë©‚Äçüëß‚Äçüë¶ Parent-Child Examples
          </h3>
          <p style={{ color: 'inherit', marginBottom: '15px' }}>
            These cards inherit their base styles from the 'card' parent. Each card only specifies its unique content and minor overrides.
          </p>

          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '20px' }}>
            {/* Card 1 - Inherits from card parent */}
            <QElementComponent elementId="card">
              <div>
                <h4 style={{ color: 'inherit', marginBottom: '10px' }}>
                  üéµ Music Card
                </h4>
                <p style={{ color: 'inherit', marginBottom: '10px' }}>
                  This card inherits all its styling from the 'card' parent. Only the content is unique.
                </p>
                <QElementComponent elementId="button">
                  <button>Play Music</button>
                </QElementComponent>
              </div>
            </QElementComponent>

            {/* Card 2 - Inherits from card parent with minor overrides */}
            <QElementComponent elementId="card">
              <div style={{
                // Child override - only specify what's different
                borderColor: '#00ff00',
                borderWidth: '2px',
              }}>
                <h4 style={{ color: 'inherit', marginBottom: '10px' }}>
                  üé® Art Card
                </h4>
                <p style={{ color: 'inherit', marginBottom: '10px' }}>
                  This card inherits from the parent but overrides the border color to green.
                </p>
                <QElementComponent elementId="button">
                  <button style={{
                    // Child override - only specify what's different
                    background: 'linear-gradient(45deg, #00ff00, #00cc00)',
                    color: '#000000',
                  }}>
                    View Art
                  </button>
                </QElementComponent>
              </div>
            </QElementComponent>

            {/* Card 3 - Inherits from card parent with more overrides */}
            <QElementComponent elementId="card">
              <div style={{
                // Child overrides - specify what's different
                background: 'linear-gradient(145deg, #4a148c 0%, #2d1b69 100%)',
                borderColor: '#ff6b6b',
                borderWidth: '3px',
              }}>
                <h4 style={{ color: 'inherit', marginBottom: '10px' }}>
                  üé≠ Drama Card
                </h4>
                <p style={{ color: 'inherit', marginBottom: '10px' }}>
                  This card overrides more properties but still inherits padding, border-radius, etc.
                </p>
                <QElementComponent elementId="button">
                  <button style={{
                    // Child override - only specify what's different
                    background: 'linear-gradient(45deg, #ff6b6b, #ff4444)',
                    borderColor: '#ffffff',
                  }}>
                    Watch Drama
                  </button>
                </QElementComponent>
              </div>
            </QElementComponent>
          </div>
        </div>
      </QElementComponent>

      {/* Explanation */}
      <QElementComponent elementId="card">
        <div>
          <h3 style={{ color: 'inherit', marginBottom: '15px' }}>
            üí° How It Works
          </h3>
          <div style={{ color: 'inherit' }}>
            <h4 style={{ marginBottom: '10px' }}>üéØ Parent Styles (ParentStyles.ts):</h4>
            <ul style={{ marginBottom: '15px', paddingLeft: '20px' }}>
              <li>Define base styles for all components</li>
              <li>Set global theme colors, fonts, spacing</li>
              <li>Control the overall look and feel</li>
            </ul>

            <h4 style={{ marginBottom: '10px' }}>üë∂ Child Components:</h4>
            <ul style={{ marginBottom: '15px', paddingLeft: '20px' }}>
              <li>Inherit all parent styles automatically</li>
              <li>Only specify what they want to override</li>
              <li>Focus on content and unique styling</li>
            </ul>

            <h4 style={{ marginBottom: '10px' }}>üîÑ Dynamic Updates:</h4>
            <ul style={{ paddingLeft: '20px' }}>
              <li>Change parent styles to affect all children</li>
              <li>No need to update individual components</li>
              <li>Centralized theme management</li>
            </ul>
          </div>
        </div>
      </QElementComponent>
    </div>
  );
};

export default ParentChildDemo;
