import React, { useState } from 'react';
import { QElementProvider, QElementComponent } from '../QElement/src';
import MusicPlatform from './App';
import CodeShowcase from './CodeShowcase';
import ParentChildDemo from './ParentChildDemo';

const MainApp = () => {
  const [activeView, setActiveView] = useState('music');

  return (
    <QElementProvider>
      <QElementComponent elementId="main-app">
        <div style={{ minHeight: '100vh' }}>
          {/* Navigation Bar */}
          <QElementComponent elementId="navigation">
            <nav style={{
              position: 'fixed',
              top: 0,
              left: 0,
              right: 0,
              zIndex: 1000,
              background: 'linear-gradient(90deg, #000000 0%, #1a0a0a 50%, #000000 100%)',
              borderBottom: '2px solid #dc2626',
              padding: '0.75rem 2rem',
              boxShadow: '0 2px 10px rgba(0,0,0,0.5)'
            }}>
              <div style={{ maxWidth: '1200px', margin: '0 auto', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
                  <h1 style={{ color: '#ffffff', margin: 0, fontSize: '1.5rem', fontWeight: 'bold' }}>
                    ðŸŽµ SoundWave Demo
                  </h1>
                  <span style={{ color: '#dc2626', fontSize: '0.8rem', fontWeight: '500' }}>
                    Powered by QElements
                  </span>
                </div>

                <div style={{ display: 'flex', gap: '1rem' }}>
                  <button
                    onClick={() => setActiveView('music')}
                    style={{
                      background: activeView === 'music' 
                        ? 'linear-gradient(45deg, #dc2626, #ff4444)' 
                        : 'transparent',
                      color: '#ffffff',
                      border: '1px solid #dc2626',
                      padding: '0.5rem 1rem',
                      borderRadius: '6px',
                      cursor: 'pointer',
                      fontSize: '0.9rem',
                      fontWeight: '500',
                      transition: 'all 0.3s ease'
                    }}
                  >
                    ðŸŽµ Music Platform
                  </button>
                  <button
                    onClick={() => setActiveView('code')}
                    style={{
                      background: activeView === 'code' 
                        ? 'linear-gradient(45deg, #dc2626, #ff4444)' 
                        : 'transparent',
                      color: '#ffffff',
                      border: '1px solid #dc2626',
                      padding: '0.5rem 1rem',
                      borderRadius: '6px',
                      cursor: 'pointer',
                      fontSize: '0.9rem',
                      fontWeight: '500',
                      transition: 'all 0.3s ease'
                    }}
                  >
                    ðŸ’» Code Showcase
                  </button>
                  <button
                    onClick={() => setActiveView('demo')}
                    style={{
                      background: activeView === 'demo' 
                        ? 'linear-gradient(45deg, #dc2626, #ff4444)' 
                        : 'transparent',
                      color: '#ffffff',
                      border: '1px solid #dc2626',
                      padding: '0.5rem 1rem',
                      borderRadius: '6px',
                      cursor: 'pointer',
                      fontSize: '0.9rem',
                      fontWeight: '500',
                      transition: 'all 0.3s ease'
                    }}
                  >
                    ðŸŽ¯ Parent-Child Demo
                  </button>
                </div>
              </div>
            </nav>
          </QElementComponent>

          {/* Main Content */}
          <QElementComponent elementId="content-area">
            <div style={{ marginTop: '80px' }}>
              {activeView === 'music' && <MusicPlatform />}
              {activeView === 'code' && <CodeShowcase />}
              {activeView === 'demo' && <ParentChildDemo />}
            </div>
          </QElementComponent>
        </div>
      </QElementComponent>
    </QElementProvider>
  );
};

export default MainApp;
