import React, { useState } from 'react';
import { QElementProvider } from './qelements';

// Import template components
import BasicTemplate from './templates/BasicTemplate';
import ThemeTemplate from './templates/ThemeTemplate';
import ResponsiveTemplate from './templates/ResponsiveTemplate';
import AnimationTemplate from './templates/AnimationTemplate';

// Sandbox Template Types
type TemplateType = 'basic' | 'theme' | 'responsive' | 'animation';

// Template configuration
const templates = [
  {
    id: 'basic' as TemplateType,
    name: 'Basic Parent-Child',
    description: 'Core QElements functionality with parent-child relationships and style inheritance',
    icon: 'üë®‚Äçüë©‚Äçüëß‚Äçüë¶',
    component: BasicTemplate
  },
  {
    id: 'theme' as TemplateType,
    name: 'Theme Management',
    description: 'Advanced theme system with dynamic color schemes and style switching',
    icon: 'üé®',
    component: ThemeTemplate
  },
  {
    id: 'responsive' as TemplateType,
    name: 'Responsive Design',
    description: 'Adaptive layouts and styles that respond to different screen sizes',
    icon: 'üì±',
    component: ResponsiveTemplate
  },
  {
    id: 'animation' as TemplateType,
    name: 'Animations',
    description: 'Smooth transitions and animations with QElements animation system',
    icon: '‚ú®',
    component: AnimationTemplate
  }
];

// Code generation for each template
const generateTemplateCode = (templateId: TemplateType, state?: any) => {
  const baseCode = {
    basic: `import React, { useState } from 'react';
import { QElementComponent, useQElementStyle } from '../qelements';

const BasicTemplate = () => {
  const { updateParent } = useQElementStyle('basic_template');
  const [parentStyle, setParentStyle] = useState({
    padding: 20,
    margin: 10,
    backgroundColor: '#f0f0f0',
    borderRadius: 8,
    border: '2px solid #ddd'
  });

  const handleParentUpdate = (newStyle) => {
    const updatedStyle = { ...parentStyle, ...newStyle };
    setParentStyle(updatedStyle);
    updateParent(updatedStyle);
  };

  return (
    <div>
      <h3>üéØ Basic Parent-Child Demo</h3>
      <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
        <button onClick={() => handleParentUpdate({ backgroundColor: '#e3f2fd' })}>
          Blue Theme
        </button>
        <button onClick={() => handleParentUpdate({ backgroundColor: '#f3e5f5' })}>
          Purple Theme
        </button>
        <button onClick={() => handleParentUpdate({ border: '3px solid #ff6b6b' })}>
          Red Border
        </button>
      </div>

      <QElementComponent elementId="basic_template">
        <h4>Parent Container</h4>
        <p>I'm the parent! My styles affect all my children.</p>
        <div style={{ marginTop: '1rem', padding: '1rem', backgroundColor: 'rgba(255,255,255,0.7)', borderRadius: 4 }}>
          <h5>Child 1</h5>
          <p>I inherit styles from my parent!</p>
        </div>
      </QElementComponent>
    </div>
  );
};

export default BasicTemplate;`,
