import React, { useState } from 'react';
import { QElementComponent, useQElementTheme } from '../qelements';

/**
 * Theme Template - Advanced Theme Management
 * 
 * This template demonstrates QElements theme system:
 * - Dynamic theme switching
 * - Theme inheritance across components
 * - Custom theme definitions
 * - Theme-aware styling
 * 
 * Edit this template to experiment with themes!
 */

const ThemeTemplate = () => {
  const { switchTheme, currentTheme: themeFromHook } = useQElementTheme();
  const [currentTheme, setCurrentTheme] = useState('default');

  const themes = {
    default: { 
      backgroundColor: '#ffffff', 
      color: '#333333',
      border: '1px solid #ddd',
      borderRadius: '8px'
    },
    dark: { 
      backgroundColor: '#2d3748', 
      color: '#ffffff',
      border: '1px solid #4a5568',
      borderRadius: '8px'
    },
    colorful: { 
      backgroundColor: '#ff6b6b', 
      color: '#ffffff',
      border: '2px solid #4ecdc4',
      borderRadius: '12px'
    },
    minimal: {
      backgroundColor: '#f8f9fa',
      color: '#495057',
      border: 'none',
      borderRadius: '4px'
    }
  };

  const handleThemeChange = (themeName: string) => {
    setCurrentTheme(themeName);
    switchTheme(themeName);
  };

  return (
    <div style={{ marginBottom: '2rem' }}>
      <h3>ðŸŽ¨ Theme Template - Theme Management</h3>
      <p>Advanced theme switching with QElements theme system.</p>

      {/* Theme Controls */}
      <div style={{ marginBottom: '1rem', display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
        <button onClick={() => handleThemeChange('default')}>
          Default Theme
        </button>
        <button onClick={() => handleThemeChange('dark')}>
          Dark Theme
        </button>
        <button onClick={() => handleThemeChange('colorful')}>
          Colorful Theme
        </button>
        <button onClick={() => handleThemeChange('minimal')}>
          Minimal Theme
        </button>
      </div>

      {/* Theme Container */}
      <QElementComponent elementId="theme_template" style={{ padding: 20 }}>
        <h4>Theme Container (Current: {currentTheme})</h4>
        <p>I automatically inherit the current theme settings.</p>

        {/* Theme Children */}
        <QElementComponent elementId="theme_template" style={{ margin: '10px 0', padding: 15 }}>
          <h5>Child 1 - Theme Inherited</h5>
          <p>I get the theme colors automatically!</p>
        </QElementComponent>

        <QElementComponent elementId="theme_template" style={{ margin: '10px 0', padding: 15 }}>
          <h5>Child 2 - Also Inherits</h5>
          <p>Theme inheritance in action!</p>
        </QElementComponent>

        <QElementComponent elementId="theme_template" style={{ margin: '10px 0', padding: 15 }}>
          <h5>Child 3 - Theme Aware</h5>
          <p>All children automatically get the theme!</p>
        </QElementComponent>
      </QElementComponent>

      {/* Theme Info */}
      <div style={{ 
        marginTop: '20px', 
        padding: '15px', 
        backgroundColor: '#f8f9fa', 
        borderRadius: '8px',
        border: '1px solid #dee2e6'
      }}>
        <h5>Current Theme Info:</h5>
        <pre style={{ fontSize: '12px', background: '#ffffff', padding: '10px', borderRadius: '4px' }}>
          {JSON.stringify(themes[currentTheme as keyof typeof themes], null, 2)}
        </pre>
      </div>
    </div>
  );
};

export default ThemeTemplate;
